// SPDX-License-Identifier: MIT
pragma solidity 0.6.12;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/SafeERC20.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Address.sol";

contract BeliToken is ERC20("Beli Token", "BELI"), Ownable {
    using SafeMath for uint256;

    address public operator;
    address public beliLpFeeReceiver;
    address public beliFeeReceiver;
    address public treasury;
    address public constant BURN_ADDRESS = 0x000000000000000000000000000000000000dEaD;
    uint16 public feeRate = 100; // 1%, transfer tax
    uint16 public constant FEE_FIRST_UPPER_LIMIT = 100; // 1%
    uint16 public constant FEE_SECOND_UPPER_LIMIT = 75; // 0.75%
    uint16 public constant FEE_THIRD_UPPER_LIMIT = 50; // 0.5%
    uint16 public constant FEE_FOURTH_UPPER_LIMIT = 25; // 0.25%

    uint16 public beliStakeFactor = 2500; // 25%, % of transfer tax to FeeReceiver
    uint16 public lpStakeFactor = 2500; // 25%, % of transfer tax to LPFeeReceiver
    uint16 public treasuryFactor = 2500; // 25%, % of transfer tax to treasury

    // anti-whale factor
    uint16 public transferRateLimit = 10000; // 1%
    uint16 public constant TRANSFER_RATE_LOWER_LIMIT = 50; // 0.5%, lower limit of maximum transfer (anti-whale)

    uint16 public constant PRECISION_FACTOR = 10000; // 100%
    uint256 public deploymentBlockNumber;

    mapping(address => bool) private excludedFromAntiWhale;

    modifier antiWhale(address _sender, address _recipient, uint256 _amount) {
        if (isExcludedWhale(_sender) == false && isExcludedWhale(_recipient) == false) {
            require(_amount <= maxTransfer(), "Transfer amount exceeded max amount allowed");
        }
        _;
    }

    modifier onlyOperator() {
        require(msg.sender == operator, "Unauthorized! Only operator may access this function");
        _;
    }

    event OperatorSet(address indexed currentOperator, address indexed newOperator);
    event WhaleWhitelistSet(address indexed operator, address indexed whale, bool value);
    event FeeRateUpdated(uint16 oldRate, uint16 newRate);
    event TransferRateLimitUpdated(uint16 oldLimit, uint16 newLimit);
    event BeliFeeReceiverUpdated(address indexed operator, address indexed newReceiver);
    event BeliLPFeeReceiverUpdated(address indexed operator, address indexed newReceiver);
    event TreasuryUpdated(address indexed operator, address indexed newTreasury);
    event BeliStakeFactorUpdated(uint16 oldFactor, uint16 newFactor);
    event BeliLPStakeFactorUpdated(uint16 oldFactor, uint16 newFactor);
    event TreasuryFactorUpdated(uint16 oldFactor, uint16 newFactor);

    constructor() public {
        operator = msg.sender;
        excludedFromAntiWhale[address(this)] = true;
        excludedFromAntiWhale[BURN_ADDRESS] = true;
        excludedFromAntiWhale[address(0)] = true;
        deploymentBlockNumber = block.number;
    }

    function feeRateUpperLimit() private view returns(uint16) {
        uint256 blockPerYear = 10512000;
        if (block.number <= deploymentBlockNumber.add(blockPerYear)) {
            return FEE_FIRST_UPPER_LIMIT;
        }
        if (block.number <= deploymentBlockNumber.add(blockPerYear.mul(2))) {
            return FEE_SECOND_UPPER_LIMIT;
        }
        if (block.number <= deploymentBlockNumber.add(blockPerYear.mul(3))) {
            return FEE_THIRD_UPPER_LIMIT;
        }
        return FEE_FOURTH_UPPER_LIMIT;
    }

    function appliedFeeRate() public view returns (uint16) {
        if (feeRate < feeRateUpperLimit()) {
            return feeRate;
        }
        return feeRateUpperLimit();
    }

    function updateTransferLimit(uint16 _newLimit) external onlyOperator {
        require(_newLimit <= PRECISION_FACTOR, "new transfer limit is too big");
        require(_newLimit >= TRANSFER_RATE_LOWER_LIMIT, "new transfer limit is too small");
        emit TransferRateLimitUpdated(transferRateLimit, _newLimit);
        transferRateLimit = _newLimit;
    }

    function updateFeeRate(uint16 _newRate) external onlyOperator {
        require(_newRate <= feeRateUpperLimit(), "fee rate is larger than max allowed value");
        emit FeeRateUpdated(feeRate, _newRate);
        feeRate = _newRate;
    }

    function updateTreasuryFactor(uint16 _newRate) external onlyOperator {
        require(_newRate <= 2500, "only maximum of 25% for treasury allowed");
        emit TreasuryFactorUpdated(treasuryFactor, _newRate);
        treasuryFactor = _newRate;
    }

    function updateBeliStakeFactor(uint16 _newRate) external onlyOperator {
        require(_newRate <= 2500, "only maximum of 25% for beli staking allowed");
        emit BeliStakeFactorUpdated(beliStakeFactor, _newRate);
        beliStakeFactor = _newRate;
    }

    function updateBeliLPStakeFactor(uint16 _newRate) external onlyOperator {
        require(_newRate <= 2500, "only maximum of 25% for LP staking allowed");
        emit BeliLPStakeFactorUpdated(lpStakeFactor, _newRate);
        lpStakeFactor = _newRate;
    }

    function isExcludedWhale(address _account) public view returns(bool) {
        return excludedFromAntiWhale[_account];
    }

    function setWhaleExclusion(address _account, bool _value) external onlyOperator {
        require(_account != address(0), "account cannot be address 0");
        excludedFromAntiWhale[_account] = _value;
        emit WhaleWhitelistSet(operator, _account, _value);
    }

    function setBeliFeeReceiver(address _feeReceiver) external onlyOperator {
        beliFeeReceiver = _feeReceiver;
        emit BeliFeeReceiverUpdated(operator, _feeReceiver);
    }

    function setBeliLPFeeReceiver(address _feeReceiver) external onlyOperator {
        beliLpFeeReceiver = _feeReceiver;
        emit BeliLPFeeReceiverUpdated(operator, _feeReceiver);
    }

    function setTreasury(address _treasury) external onlyOperator {
        treasury = _treasury;
        emit TreasuryUpdated(operator, _treasury);
    }

    function setOperator(address _newOperator) external onlyOperator {
        emit OperatorSet(operator, _newOperator);
        operator = _newOperator;
    }

    function maxTransfer() public view returns (uint256) {
        return totalSupply().mul(transferRateLimit).div(PRECISION_FACTOR);
    }

    function mint(address _to, uint256 _amount) public onlyOwner {
        _mint(_to, _amount);
    }

    /**
     * @dev
     * setting a fee scheme upon transfer
     * 1. A maximum of 25% goes to treasury, for future funding or a possibility of bridging
     * 2. A maximum of 25% goes to extra reward for BELI staking to incentivize our staker
     * 3. A maximum of 25% goes to extra reward for our LP staking
     * 4. Finally the remainder of the specified fee will be burnt away.
     */
    function _transfer(address _sender, address _recipient, uint256 _amount) internal virtual override antiWhale(_sender, _recipient, _amount) {
        if (_recipient == BURN_ADDRESS) {
            super._transfer(_sender, _recipient, _amount);
        } else {
            uint256 burnAmount;
            uint256 treasuryFee;
            uint256 beliStakeAmount;
            uint256 lpStakeAmount; // BELI LP
            uint256 feeCut = _amount.mul(appliedFeeRate()).div(PRECISION_FACTOR);
            if (treasury != address(0)) {
                treasuryFee = feeCut.mul(treasuryFactor).div(PRECISION_FACTOR);
            }
            if (beliLpFeeReceiver != address(0)) {
                lpStakeAmount = feeCut.mul(lpStakeFactor).div(PRECISION_FACTOR);
            }
            if (beliFeeReceiver != address(0)) {
                beliStakeAmount = feeCut.mul(beliStakeFactor).div(PRECISION_FACTOR);
            }
            burnAmount = feeCut.sub(treasuryFee).sub(beliStakeAmount).sub(lpStakeAmount);

            uint256 sendAmount = _amount.sub(feeCut);
            if (burnAmount > 0) {
                super._transfer(_sender, BURN_ADDRESS, burnAmount);
            }
            if (treasuryFee > 0) {
                super._transfer(_sender, treasury, treasuryFee);
            }
            if (beliStakeAmount > 0) {
                super._transfer(_sender, beliFeeReceiver, beliStakeAmount);
            }
            if (lpStakeAmount > 0) {
                super._transfer(_sender, beliLpFeeReceiver, lpStakeAmount);
            }
            super._transfer(_sender, _recipient, sendAmount);
        }
    }
}
